// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

import "../../openzeppelin-solidity/contracts/SafeMath.sol";

contract Timelock {
    using SafeMath for uint;

    event NewAdmin(address indexed newAdmin);
    event NewPendingAdmin(address indexed newPendingAdmin);
    event NewDelay(uint indexed newDelay);
    event CancelTransaction(bytes32 indexed txHash, address indexed target, uint value, string signature,  bytes data, uint eta);
    event ExecuteTransaction(bytes32 indexed txHash, address indexed target, uint value, string signature,  bytes data, uint eta);
    event QueueTransaction(bytes32 indexed txHash, address indexed target, uint value, string signature, bytes data, uint eta);

    uint public constant GRACE_PERIOD = 14 days;
    uint public constant MINIMUM_DELAY = 2 days;
    uint public constant MAXIMUM_DELAY = 30 days;

    address public admin;
    address public pendingAdmin;
    uint public delay;

    mapping (bytes32 => bool) public queuedTransactions;


    constructor(address admin_, uint delay_) {
        require(delay_ >= MINIMUM_DELAY, "Timelock::constructor: Delay must exceed minimum delay.");
        require(delay_ <= MAXIMUM_DELAY, "Timelock::setDelay: Delay must not exceed maximum delay.");

        admin = admin_;
        delay = delay_;
    }

    receive() external payable { }

    function setDelay(uint delay_) public {
        require(msg.sender == address(this), "Timelock::setDelay: Call must come from Timelock.");
        require(delay_ >= MINIMUM_DELAY, "Timelock::setDelay: Delay must exceed minimum delay.");
        require(delay_ <= MAXIMUM_DELAY, "Timelock::setDelay: Delay must not exceed maximum delay.");
        delay = delay_;

        emit NewDelay(delay);
    }

    function acceptAdmin() public {
        require(msg.sender == pendingAdmin, "Timelock::acceptAdmin: Call must come from pendingAdmin.");
        admin = msg.sender;
        pendingAdmin = address(0);

        emit NewAdmin(admin);
    }

    function setPendingAdmin(address pendingAdmin_) public {
        require(msg.sender == address(this), "Timelock::setPendingAdmin: Call must come from Timelock.");
        pendingAdmin = pendingAdmin_;

        emit NewPendingAdmin(pendingAdmin);
    }

    function queueTransaction(address target, uint value, string memory signature, bytes memory data, uint eta) public returns (bytes32) {
        require(msg.sender == admin, "Timelock::queueTransaction: Call must come from admin.");
        require(eta >= getBlockTimestamp().add(delay), "Timelock::queueTransaction: Estimated execution block must satisfy delay.");

        bytes32 txHash = keccak256(abi.encode(target, value, signature, data, eta));
        queuedTransactions[txHash] = true;

        emit QueueTransaction(txHash, target, value, signature, data, eta);
        return txHash;
    }

    function cancelTransaction(address target, uint value, string memory signature, bytes memory data, uint eta) public {
        require(msg.sender == admin, "Timelock::cancelTransaction: Call must come from admin.");

        bytes32 txHash = keccak256(abi.encode(target, value, signature, data, eta));
        queuedTransactions[txHash] = false;

        emit CancelTransaction(txHash, target, value, signature, data, eta);
    }

    function executeTransaction(address target, uint value, string memory signature, bytes memory data, uint eta) public payable returns (bytes memory) {
        require(msg.sender == admin, "Timelock::executeTransaction: Call must come from admin.");

        bytes32 txHash = keccak256(abi.encode(target, value, signature, data, eta));
        require(queuedTransactions[txHash], "Timelock::executeTransaction: Transaction hasn't been queued.");
        require(getBlockTimestamp() >= eta, "Timelock::executeTransaction: Transaction hasn't surpassed time lock.");
        require(getBlockTimestamp() <= eta.add(GRACE_PERIOD), "Timelock::executeTransaction: Transaction is stale.");

        queuedTransactions[txHash] = false;

        bytes memory callData;

        if (bytes(signature).length == 0) {
            callData = data;
        } else {
            callData = abi.encodePacked(bytes4(keccak256(bytes(signature))), data);
        }

        // solium-disable-next-line security/no-call-value
        (bool success, bytes memory returnData) = target.call{value: value}(callData);
        require(success, "Timelock::executeTransaction: Transaction execution reverted.");

        emit ExecuteTransaction(txHash, target, value, signature, data, eta);

        return returnData;
    }

    function getBlockTimestamp() internal view returns (uint) {
        // solium-disable-next-line security/no-block-members
        return block.timestamp;
    }
}

contracts/voting/LinearReleaseToken.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

import "../openzeppelin-solidity/contracts/Math.sol";
import "../openzeppelin-solidity/contracts/SafeMath.sol";
import "../openzeppelin-solidity/contracts/Ownable.sol";
import "../openzeppelin-solidity/contracts/SafeERC20.sol";
import "../openzeppelin-solidity/contracts/ReentrancyGuard.sol";

import "./VotingToken.sol";
import "../interfaces/IReleaseToken.sol";

/**
 * Voting, non-transferrable token with a linear release schedule.
 */
contract LinearReleaseToken is
    VotingToken,
    Ownable,
    IReleaseToken,
    ReentrancyGuard
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /// @notice Timestamp of when the release starts.
    uint256 public immutable startTime;

    /// @notice Timestamp of when the cliff ends.
    uint256 public immutable cliffEndTime;

    /// @notice Timestamp of when the release ends.
    uint256 public immutable endTime;

    /// @notice Token to release
    IERC20 public immutable token;

    /// @notice Unallocated share
    uint96 public unallocated;

    /// @notice The total number of tokens ever allocated to each address.
    mapping(address => uint96) public lifetimeTotalAllocated;

    /// @notice The total number of tokens each address ever claimed.
    mapping(address => uint96) public totalClaimed;

    /**
     * @notice Creates a LinearReleaseToken. Transfer `amount_` tokens to this contract after it's deployed.
     *
     * @param name_ Name of the ERC20 token
     * @param symbol_ Symbol of the ERC20 token
     * @param decimals_ Decimals of the ERC20 token
     * @param owner_ who can send tokens to others
     * @param token_ the token that is released
     * @param amount_ amount of tokens to lock up (max 96 bits, i.e. 2 billion tokens with 18 decimals)
     * @param startTime_ when release starts
     * @param cliffEndTime_ when the cliff starts. If set to 0, this does not take effect.
     * @param endTime_ when release ends
     */
    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        address owner_,
        address token_,
        uint96 amount_,
        uint256 startTime_,
        uint256 cliffEndTime_,
        uint256 endTime_
    ) VotingToken(name_, symbol_, decimals_) {
        transferOwnership(owner_);
        token = IERC20(token_);
        unallocated = amount_;
        startTime = startTime_;
        cliffEndTime = cliffEndTime_;
        endTime = endTime_;
    }

    /**
     * Allocates release tokens to the specified holders.
     * @param _holders Array of holders of release tokens
     * @param _amounts Array of amounts of tokens to issue
     */
    function allocate(address[] calldata _holders, uint96[] calldata _amounts)
        external
        override
        onlyOwner
    {
        require(
            _holders.length == _amounts.length,
            "LinearReleaseToken: length mismatch"
        );
        require(
            _holders.length <= 20,
            "LinearReleaseToken: max 20 holders at initial allocation"
        );
        for (uint8 i = 0; i < _holders.length; i++) {
            _allocate(_holders[i], _amounts[i]);
        }
    }

    function _allocate(address _holder, uint96 _amount) internal {
        unallocated = sub96(
            unallocated,
            _amount,
            "LinearReleaseToken::_allocate: overallocated"
        );
        lifetimeTotalAllocated[_holder] = add96(
            lifetimeTotalAllocated[_holder],
            _amount,
            "LinearReleaseToken::_allocate: total allocation overflow"
        );

        _mintVotes(_holder, _amount);
        emit Allocated(_holder, _amount);
    }

    /**
     * Computes the number of tokens that the address can redeem.
     */
    function earned(address _owner) public view override returns (uint96) {
        // compute the total amount of tokens earned if this holder never claimed
        uint96 earnedIfNeverClaimed =
            releasableSupplyOfPrincipal(lifetimeTotalAllocated[_owner]);
        if (earnedIfNeverClaimed == 0) {
            return 0;
        }

        // subtract the total already claimed by the address
        return
            sub96(
                earnedIfNeverClaimed,
                totalClaimed[_owner],
                "LinearReleaseToken: earned invalid"
            );
    }

    /**
     * The total amount of UBE tokens that can be redeemed if all
     * tokens were distributed.
     */
    function releasableSupply() public view returns (uint96) {
        return
            releasableSupplyOfPrincipal(
                add96(
                    totalVotingPower(),
                    unallocated,
                    "LinearReleaseToken::releasableSupply: overflow"
                )
            );
    }

    /**
     * Computes the releasable supply of the given principal amount.
     */
    function releasableSupplyOfPrincipal(uint256 _principal)
        public
        view
        returns (uint96)
    {
        // solhint-disable-next-line not-rely-on-time
        if (block.timestamp < startTime || block.timestamp < cliffEndTime) {
            return 0;
        }
        uint256 secondsSinceStart =
            // solhint-disable-next-line not-rely-on-time
            Math.min(block.timestamp, endTime).sub(startTime);
        return
            safe96(
                uint256(_principal).mul(secondsSinceStart).div(
                    endTime.sub(startTime)
                ),
                "LinearReleaseToken::circulatingSupply: invalid amount"
            );
    }

    /**
     * Claims any tokens that the sender is entitled to.
     */
    function claim() public override nonReentrant {
        uint96 amount = earned(msg.sender);
        if (amount == 0) {
            // don't do anything if the sender has no tokens.
            return;
        }

        totalClaimed[msg.sender] = add96(
            totalClaimed[msg.sender],
            amount,
            "LinearReleaseToken::claim: total claimed overflow"
        );
        _burnVotes(msg.sender, amount);
        token.safeTransfer(msg.sender, amount);
        emit Claimed(msg.sender, amount);
    }
}

contracts/voting/TransferrableVotingToke.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

import "./VotingToken.sol";

contract TransferrableVotingToken is VotingToken {
    /**
     * @dev Sets the values for `name`, `symbol`, and `decimals`. All three of
     * these values are immutable: they can only be set once during
     * construction.
     * @param initialSupply_ Initial supply of tokens
     * @param account_ The initial account to grant all the tokens
     */
    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint96 initialSupply_,
        address account_
    ) VotingToken(name_, symbol_, decimals_) {
        _mintVotes(account_, initialSupply_);
    }

    ////////////////////////////////
    //
    // The below code is copied from Uniswap's Uni.sol.
    // Changes are marked with "XXX".
    //
    ////////////////////////////////

    // XXX: deleted name, symbol, decimals, totalSupply, minter, mintingAllowedAfter,
    // minimumTimeBetweenMints, mintCap

    // Allowance amounts on behalf of others
    mapping (address => mapping (address => uint96)) internal allowances;

    // XXX: balances, delegates, Checkpoint, checkpoints,
    // numCheckpoints, DOMAIN_TYPEHASH, DELEGATION_TYPEHASH
    // are inherited from VotingPower.sol

    /// @notice The EIP-712 typehash for the permit struct used by the contract
    bytes32 public constant PERMIT_TYPEHASH = keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");

    // XXX: nonces is inherited from VotingPower.sol

    // XXX: deleted MinterChanged

    // XXX: deleted DelegateChanged, DelegateVotesChanged, Transfer and moved them to IVotingPower

    /// @notice The standard EIP-20 approval event
    event Approval(address indexed owner, address indexed spender, uint256 amount);

    // XXX: deleted constructor, setMinter, mint

    /**
     * @notice Get the number of tokens `spender` is approved to spend on behalf of `account`
     * @param account The address of the account holding the funds
     * @param spender The address of the account spending the funds
     * @return The number of tokens approved
     */
    function allowance(address account, address spender) external view returns (uint) {
        return allowances[account][spender];
    }

    // XXX_ADDED: upgrade to Solidity 0.8.3, which doesn't allow use of uintn(-1)
    uint256 internal constant MAX_INT = 2**256 - 1;
    uint96 internal constant MAX_INT_96 = 2**96 - 1;

    /**
     * @notice Approve `spender` to transfer up to `amount` from `src`
     * @dev This will overwrite the approval amount for `spender`
     *  and is subject to issues noted [here](https://eips.ethereum.org/EIPS/eip-20#approve)
     * @param spender The address of the account which may transfer tokens
     * @param rawAmount The number of tokens that are approved (2^256-1 means infinite)
     * @return Whether or not the approval succeeded
     */
    function approve(address spender, uint rawAmount) external returns (bool) {
        uint96 amount;
        // XXX: uint256(-1) => MAX_INT
        if (rawAmount == MAX_INT) {
            // XXX: uint96(-1) => MAX_INT_96
            amount = MAX_INT_96;
        } else {
            amount = safe96(rawAmount, "Uni::approve: amount exceeds 96 bits");
        }

        allowances[msg.sender][spender] = amount;

        emit Approval(msg.sender, spender, amount);
        return true;
    }

    /**
     * @notice Triggers an approval from owner to spends
     * @param owner The address to approve from
     * @param spender The address to be approved
     * @param rawAmount The number of tokens that are approved (2^256-1 means infinite)
     * @param deadline The time at which to expire the signature
     * @param v The recovery byte of the signature
     * @param r Half of the ECDSA signature pair
     * @param s Half of the ECDSA signature pair
     */
    function permit(address owner, address spender, uint rawAmount, uint deadline, uint8 v, bytes32 r, bytes32 s) external {
        uint96 amount;
        // XXX: uint256(-1) => MAX_INT
        if (rawAmount == MAX_INT) {
            // XXX: uint96(-1) => MAX_INT_oy
            amount = MAX_INT_96;
        } else {
            amount = safe96(rawAmount, "Uni::permit: amount exceeds 96 bits");
        }

        // XXX_CHANGED: name => name()
        bytes32 domainSeparator = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes(name())), getChainId(), address(this)));
        bytes32 structHash = keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, rawAmount, nonces[owner]++, deadline));
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));
        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "Uni::permit: invalid signature");
        require(signatory == owner, "Uni::permit: unauthorized");
        // XXX: added linter disable
        // solhint-disable-next-line not-rely-on-time
        require(block.timestamp <= deadline, "Uni::permit: signature expired");

        allowances[owner][spender] = amount;

        emit Approval(owner, spender, amount);
    }

    // XXX: deleted balanceOf

    /**
     * @notice Transfer `amount` tokens from `msg.sender` to `dst`
     * @param dst The address of the destination account
     * @param rawAmount The number of tokens to transfer
     * @return Whether or not the transfer succeeded
     */
    function transfer(address dst, uint rawAmount) external returns (bool) {
        // XXX_ADDED
        require(
            dst != address(this),
            "TransferrableVotingToken::transfer: cannot send tokens to contract"
        );
        uint96 amount = safe96(rawAmount, "Uni::transfer: amount exceeds 96 bits");
        _transferTokens(msg.sender, dst, amount);
        return true;
    }

    /**
     * @notice Transfer `amount` tokens from `src` to `dst`
     * @param src The address of the source account
     * @param dst The address of the destination account
     * @param rawAmount The number of tokens to transfer
     * @return Whether or not the transfer succeeded
     */
    function transferFrom(address src, address dst, uint rawAmount) external returns (bool) {
        // XXX_ADDED
        require(
            dst != address(this),
            "TransferrableVotingToken::transferFrom: cannot send tokens to contract"
        );
        address spender = msg.sender;
        uint96 spenderAllowance = allowances[src][spender];
        uint96 amount = safe96(rawAmount, "Uni::approve: amount exceeds 96 bits");

        // XXX: uint96(-1) => MAX_INT_96
        if (spender != src && spenderAllowance != MAX_INT_96) {
            uint96 newAllowance = sub96(spenderAllowance, amount, "Uni::transferFrom: transfer amount exceeds spender allowance");
            allowances[src][spender] = newAllowance;

            emit Approval(src, spender, newAllowance);
        }

        _transferTokens(src, dst, amount);
        return true;
    }

    // XXX: rest is in VotingPower.sol
}

contracts/voting/VotingPower.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

import "../interfaces/IHasVotes.sol";
import "../interfaces/IVotingDelegates.sol";

/**
 * Power to vote. Heavily based on Uni.
 */
contract VotingPower is IHasVotes, IVotingDelegates {
    // Name of the token. This cannot be changed after creating the token.
    string private _name;

    // Total amount of voting power available.
    uint96 private totalVotingPowerSupply;

    constructor(string memory name_) {
        _name = name_;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @notice Mint new voting power
     * @param dst The address of the destination account
     * @param amount The amount of voting power to be minted
     */
    function _mintVotes(address dst, uint96 amount) internal {
        require(dst != address(0), "VotingPower::_mintVotes: cannot mint to the zero address");

        // transfer the amount to the recipient
        balances[dst] = add96(balances[dst], amount, "VotingPower::_mintVotes: mint amount overflows");
        totalVotingPowerSupply = add96(
            totalVotingPowerSupply, amount, "VotingPower::_mintVotes: total supply overflows"
        );
        emit Transfer(address(0), dst, amount);

        // move delegates
        _moveDelegates(address(0), delegates[dst], amount);
    }

    /**
     * @notice Burn voting power
     * @param src The address of the source account
     * @param amount The amount of voting power to be burned
     */
    function _burnVotes(address src, uint96 amount) internal {
        require(src != address(0), "VotingPower::_burnVotes: cannot burn from the zero address");

        // transfer the amount to the recipient
        balances[src] = sub96(balances[src], amount, "VotingPower::_burnVotes: burn amount underflows");
        totalVotingPowerSupply = sub96(
            totalVotingPowerSupply, amount, "VotingPower::_burnVotes: total supply underflows"
        );
        emit Transfer(src, address(0), amount);

        // move delegates
        _moveDelegates(delegates[src], address(0), amount);
    }

    /**
     * @notice Get the amount of voting power of an account
     * @param account The address of the account to get the balance of
     * @return The amount of voting power held
     */
    function votingPower(address account) public view override returns (uint96) {
        return balances[account];
    }

    function totalVotingPower() public view override returns (uint96) {
        return totalVotingPowerSupply;
    }

    ////////////////////////////////
    //
    // The below code is copied from ../uniswap-governance/contracts/Uni.sol.
    // Changes are marked with "XXX".
    //
    ////////////////////////////////

    // XXX: deleted name, symbol, decimals, totalSupply, minter, mintingAllowedAfter,
    // minimumTimeBetweenMints, mintCap, allowances

    // Official record of token balances for each account
    // XXX: internal => private visibility
    mapping (address => uint96) private balances;

    /// @notice A record of each accounts delegate
    mapping (address => address) public override delegates;

    /// @notice A checkpoint for marking number of votes from a given block
    struct Checkpoint {
        uint32 fromBlock;
        uint96 votes;
    }

    /// @notice A record of votes checkpoints for each account, by index
    mapping (address => mapping (uint32 => Checkpoint)) public checkpoints;

    /// @notice The number of checkpoints for each account
    mapping (address => uint32) public numCheckpoints;

    /// @notice The EIP-712 typehash for the contract's domain
    bytes32 public constant DOMAIN_TYPEHASH = keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)");

    /// @notice The EIP-712 typehash for the delegation struct used by the contract
    bytes32 public constant DELEGATION_TYPEHASH = keccak256("Delegation(address delegatee,uint256 nonce,uint256 expiry)");

    // XXX: deleted PERMIT_TYPEHASH

    /// @notice A record of states for signing / validating signatures
    mapping (address => uint) public nonces;

    // XXX: deleted MinterChanged

    // XXX: deleted DelegateChanged, DelegateVotesChanged, Transfer and moved them to IVotingPower

    // XXX: deleted Approval

    // XXX: deleted constructor, setMinter, mint, allowance, approve, permit, balanceOf

    // XXX: deleted transfer, transferFrom

    /**
     * @notice Delegate votes from `msg.sender` to `delegatee`
     * @param delegatee The address to delegate votes to
     */
    function delegate(address delegatee) public override {
        return _delegate(msg.sender, delegatee);
    }

    /**
     * @notice Delegates votes from signatory to `delegatee`
     * @param delegatee The address to delegate votes to
     * @param nonce The contract state required to match the signature
     * @param expiry The time at which to expire the signature
     * @param v The recovery byte of the signature
     * @param r Half of the ECDSA signature pair
     * @param s Half of the ECDSA signature pair
     */
    function delegateBySig(address delegatee, uint nonce, uint expiry, uint8 v, bytes32 r, bytes32 s) public override {
        // XXX_CHANGED: name => _name
        bytes32 domainSeparator = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes(_name)), getChainId(), address(this)));
        bytes32 structHash = keccak256(abi.encode(DELEGATION_TYPEHASH, delegatee, nonce, expiry));
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));
        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "Uni::delegateBySig: invalid signature");
        require(nonce == nonces[signatory]++, "Uni::delegateBySig: invalid nonce");
        // XXX: added linter disable
        // solhint-disable-next-line not-rely-on-time
        require(block.timestamp <= expiry, "Uni::delegateBySig: signature expired");
        return _delegate(signatory, delegatee);
    }

    /**
     * @notice Gets the current votes balance for `account`
     * @param account The address to get votes balance
     * @return The number of current votes for `account`
     */
    function getCurrentVotes(address account) external view override returns (uint96) {
        uint32 nCheckpoints = numCheckpoints[account];
        return nCheckpoints > 0 ? checkpoints[account][nCheckpoints - 1].votes : 0;
    }

    /**
     * @notice Determine the prior number of votes for an account as of a block number
     * @dev Block number must be a finalized block or else this function will revert to prevent misinformation.
     * @param account The address of the account to check
     * @param blockNumber The block number to get the vote balance at
     * @return The number of votes the account had as of the given block
     */
    function getPriorVotes(address account, uint blockNumber) public view override returns (uint96) {
        require(blockNumber < block.number, "Uni::getPriorVotes: not yet determined");

        uint32 nCheckpoints = numCheckpoints[account];
        if (nCheckpoints == 0) {
            return 0;
        }

        // First check most recent balance
        if (checkpoints[account][nCheckpoints - 1].fromBlock <= blockNumber) {
            return checkpoints[account][nCheckpoints - 1].votes;
        }

        // Next check implicit zero balance
        if (checkpoints[account][0].fromBlock > blockNumber) {
            return 0;
        }

        uint32 lower = 0;
        uint32 upper = nCheckpoints - 1;
        while (upper > lower) {
            uint32 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            Checkpoint memory cp = checkpoints[account][center];
            if (cp.fromBlock == blockNumber) {
                return cp.votes;
            } else if (cp.fromBlock < blockNumber) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return checkpoints[account][lower].votes;
    }

    function _delegate(address delegator, address delegatee) internal {
        address currentDelegate = delegates[delegator];
        uint96 delegatorBalance = balances[delegator];
        delegates[delegator] = delegatee;

        emit DelegateChanged(delegator, currentDelegate, delegatee);

        _moveDelegates(currentDelegate, delegatee, delegatorBalance);
    }

    function _transferTokens(address src, address dst, uint96 amount) internal {
        require(src != address(0), "Uni::_transferTokens: cannot transfer from the zero address");
        require(dst != address(0), "Uni::_transferTokens: cannot transfer to the zero address");

        balances[src] = sub96(balances[src], amount, "Uni::_transferTokens: transfer amount exceeds balance");
        balances[dst] = add96(balances[dst], amount, "Uni::_transferTokens: transfer amount overflows");
        emit Transfer(src, dst, amount);

        _moveDelegates(delegates[src], delegates[dst], amount);
    }

    function _moveDelegates(address srcRep, address dstRep, uint96 amount) internal {
        if (srcRep != dstRep && amount > 0) {
            if (srcRep != address(0)) {
                uint32 srcRepNum = numCheckpoints[srcRep];
                uint96 srcRepOld = srcRepNum > 0 ? checkpoints[srcRep][srcRepNum - 1].votes : 0;
                uint96 srcRepNew = sub96(srcRepOld, amount, "Uni::_moveVotes: vote amount underflows");
                _writeCheckpoint(srcRep, srcRepNum, srcRepOld, srcRepNew);
            }

            if (dstRep != address(0)) {
                uint32 dstRepNum = numCheckpoints[dstRep];
                uint96 dstRepOld = dstRepNum > 0 ? checkpoints[dstRep][dstRepNum - 1].votes : 0;
                uint96 dstRepNew = add96(dstRepOld, amount, "Uni::_moveVotes: vote amount overflows");
                _writeCheckpoint(dstRep, dstRepNum, dstRepOld, dstRepNew);
            }
        }
    }

    function _writeCheckpoint(address delegatee, uint32 nCheckpoints, uint96 oldVotes, uint96 newVotes) internal {
      uint32 blockNumber = safe32(block.number, "Uni::_writeCheckpoint: block number exceeds 32 bits");

      if (nCheckpoints > 0 && checkpoints[delegatee][nCheckpoints - 1].fromBlock == blockNumber) {
          checkpoints[delegatee][nCheckpoints - 1].votes = newVotes;
      } else {
          checkpoints[delegatee][nCheckpoints] = Checkpoint(blockNumber, newVotes);
          numCheckpoints[delegatee] = nCheckpoints + 1;
      }

      emit DelegateVotesChanged(delegatee, oldVotes, newVotes);
    }

    function safe32(uint n, string memory errorMessage) internal pure returns (uint32) {
        require(n < 2**32, errorMessage);
        return uint32(n);
    }

    function safe96(uint n, string memory errorMessage) internal pure returns (uint96) {
        require(n < 2**96, errorMessage);
        return uint96(n);
    }

    function add96(uint96 a, uint96 b, string memory errorMessage) internal pure returns (uint96) {
        uint96 c = a + b;
        require(c >= a, errorMessage);
        return c;
    }

    function sub96(uint96 a, uint96 b, string memory errorMessage) internal pure returns (uint96) {
        require(b <= a, errorMessage);
        return a - b;
    }

    function getChainId() internal view returns (uint) {
        uint256 chainId;
        // XXX: added linter disable
        // solhint-disable-next-line no-inline-assembly
        assembly { chainId := chainid() }
        return chainId;
    }
}

contracts/voting/VotingToken.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.3;

import "../interfaces/INonTransferrableToken.sol";
import "./VotingPower.sol";

/**
 * A non-transferrable token that can vote.
 */
contract VotingToken is INonTransferrableToken, VotingPower {
    string private _symbol;
    uint8 private immutable _decimals;

    /**
     * @dev Sets the values for `name`, `symbol`, and `decimals`. All three of
     * these values are immutable: they can only be set once during
     * construction.
     */
    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_
    ) VotingPower(name_) {
        _symbol = symbol_;
        _decimals = decimals_;
    }

    function name()
        public
        view
        override(INonTransferrableToken, VotingPower)
        returns (string memory)
    {
        return VotingPower.name();
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return totalVotingPower();
    }

    function balanceOf(address _account)
        public
        view
        override
        returns (uint256)
    {
        return votingPower(_account);
    }
}
